import * as React from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { withStyles } from '@mui/styles';
import {data} from '../../MockData/MockData'
import MaterialTable from 'material-table';
import DeleteOutlineIcon from '@mui/icons-material/DeleteOutline';
import './ShoppingList.css'
import TableComponent from '../TableComponent/TableComponent';
const styles = theme => ({
  root: {
    display: 'flex',
    marginTop: theme.spacing?.unit * 3,
    overflowX: 'hide',
  },
  table: {
    minWidth: 340,
  },
  tableCell: {
    paddingRight: 4,
    paddingLeft: 5
  }
});
function ShoppingList(props) {
  const [prodList, setProdList] = React.useState(data.products)
  const { classes } = props;
  const handleDelete = (index) => {
    prodList.splice(index, 1)
    setProdList(prodList)
    console.log('prod ', prodList)
  }
  React.useEffect(() => {
    const newArr = prodList.map((val) => ({...val, quantity: 0}))
    setProdList(newArr)
  }, [])
  const handleQuanity = (id, format) => {
    let changedVal = prodList.map((val) => {
      if(val.quantity > 0 && val.id === id) {
        format === 'inc' ? val.quantity++ : val.quantity--
      }
      return val
    })
    setProdList(changedVal)
  }
  console.log('productList -- ', prodList)
  return (<TableContainer id='shopping-list' component={Paper}>
    <MaterialTable
    title="Simple Action Preview"
    className={classes.root}
    columns={[
      { title: 'Product', field: 'name', render: (val) => {
        return <div><p className='prod-img'><img src={require(`../../static/images/${val.imageUrl}`).default} alt={val.name} /></p>
        <div>{val.name}<br />
          {val?.desc}</div>
        </div>
      }
     },
      { title: 'Price', field: 'price', render:  (row) => (row.price + ' $') },
      { title: 'Quantity', field: 'quantity', render: (row) => <div style={{display: 'inline'}}>
      <span onClick={() => handleQuanity(row.id, 'dec')}><img src={row.quantity === 0 ? require('../../static/icon/minus.png').default : require('../../static/icon/minus-active.png').default} alt='minus' /></span>
      <span style={{border: '1px solid #777', padding: 10}}>{row?.quantity}</span>
      <span onClick={() => handleQuanity(row.id, 'inc')}><img src={require('../../static/icon/plus.png').default} alt='plus' /> </span>
  </div> },
      {
        title: 'Subtotal',
        field: 'subtotal',
        render: (row) => <div>{row.price * (row.quantity + 1) + '  $'}</div>
      },
    ]}
    data={prodList}
    options={{showTitle: false, paging: false, search: false, actionsColumnIndex: -1}}
    editable={{
        onRowDelete: oldData =>
            new Promise(async(resolve, reject) => {
                console.log('lllllll -- ', oldData)
                setTimeout(() => {
                    const dataDelete = [...prodList];
                    const index = oldData.tableData.id;
                    dataDelete.splice(index, 1);
                    setProdList([...dataDelete]);

                    resolve();
                }, 1000);
            })
    }}
  />
    </TableContainer>
    
  );
}
export default withStyles(styles)(ShoppingList);
