import React, { useEffect } from 'react'
import MaterialTable from 'material-table';
import DeleteOutlineIcon from '@mui/icons-material/DeleteOutline';
function TableComponent(props) {
    const {data, createData, handleDelete, classes} = props
  const tableRef = React.createRef();
    useEffect(()=> {
        console.log('reloading')
    }, [data])
    return <MaterialTable
    title="Simple Action Preview"
    className={classes.root}
    tableRef={tableRef}
    columns={[
      { title: 'Product', field: 'name' },
      { title: 'Price', field: 'imageUrl' },
      { title: 'Quantity', field: 'price', type: 'numeric' },
      {
        title: 'Subtotal',
        field: 'birthCity'
      },
    ]}
    data={createData}
    actions={[
      {
        icon: () => <DeleteOutlineIcon />,
        tooltip: 'Delete Product',
        onClick: (event, rowData) => handleDelete(rowData.id - 1)
      }
    ]}
    options={{paging: false, search: false, actionsColumnIndex: -1}}
  />
}
export default TableComponent;
