import React from "react";
import TextField from "@mui/material/TextField";
import LocationOnOutlinedIcon from "@mui/icons-material/LocationOnOutlined";
import InputAdornment from "@mui/material/InputAdornment";
import "./ShoppingList.css";

export default function DeliverOperation(props) {
    const {prodList, data, subTot} = props
    let discountPrice = Number(subTot) * (Number(data.discount.discountPercentage) / 100)
    const defImage = <img src={require("../../static/icon/check.png").default}
    alt="check"
  />
    return <><div className="availability">
    <h4>Deliver Availability</h4>
    <TextField
      id="input-with-icon-textfield"
      type="number"
      InputProps={{
        startAdornment: (
          <InputAdornment position="start">
            <LocationOnOutlinedIcon />
          </InputAdornment>
        ),
      }}
      variant="standard"
    />
    <div className='deliver-options'>
      <span>{defImage} &nbsp;Free Delivery</span>
      <span>{defImage} &nbsp;Cash on Delivery</span>
      <span>{defImage} &nbsp;Estimated Delivery Time is 3-5 days.</span>
    </div>
  </div>
  <div className="order-delivery">
    <h4>Order Summary ({prodList.length} items)</h4>
    <div style={{margin: '15px'}}>
      <span className='field-label'>Subtotal</span>
      <span style={{ textAlign: "right" }}>{subTot + "$"}</span>
    </div>
    <div style={{margin: '15px'}}>
      <span className='field-label'>Total Discount</span>
      {subTot >= data.discount.minTotal ? (
        <span style={{ textAlign: "right" }}>
          {-(
            discountPrice
          ) + "$"}
        </span>
      ) : (
        0
      )}
    </div>
    <div  style={{margin: '15px'}}><span className='field-label'>Standard Shipping</span>
    <span style={{ textAlign: "right" }}>Free</span></div>
    <div style={{margin: '15px'}}><h4 className='field-label'>Order Total</h4>
    <span style={{ textAlign: "right", marginTop: '1rem' }}>{subTot - discountPrice}</span>
  </div>
  </div>

  </>
}
