import * as React from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { withStyles } from '@mui/styles';
import {data} from '../../MockData/MockData'
import MaterialTable from 'material-table';
import './ShoppingList.css'
const styles = theme => ({
  root: {
    display: 'flex',
    marginTop: theme.spacing?.unit * 3,
    overflowX: 'hide',
  },
  table: {
    minWidth: 340,
  },
  tableCell: {
    paddingRight: 4,
    paddingLeft: 5
  }
});
function ShoppingList(props) {
  const [prodList, setProdList] = React.useState(data.products)
  const { classes } = props;
  const handleDelete = (index) => {
    prodList.splice(index, 1)
    setProdList(prodList)
    console.log('prod ', prodList)
  }
  React.useEffect(() => {}, [prodList])
  const tableRef = React.createRef();
  const createData = prodList.map((row, index) => {
    return {
      ...row,
      name: <div><p className='prod-img'><img src={require(`../../static/images/${row.imageUrl}`).default} alt={row.name} /></p>
      <div>{row.name}<br />
        {row?.desc}</div>
      </div>,
      price: row.price + ' $'
    }
  })  
  return (<TableContainer id='shopping-list' component={Paper}>
    <MaterialTable
      title="Simple Action Preview"
      className={classes.root}
      tableRef={tableRef}
      columns={[
        { title: 'Product', field: 'name' },
        { title: 'Price', field: 'imageUrl' },
        { title: 'Quantity', field: 'price', type: 'numeric' },
        {
          title: 'Subtotal',
          field: 'birthCity'
        },
      ]}
      data={createData}
      actions={[
        {
          icon: 'delete',
          tooltip: 'Delete Product',
          onClick: (event, rowData) => handleDelete(rowData.id - 1)
        }
      ]}
      options={{paging: false, search: false, actionsColumnIndex: -1}}
    /></TableContainer>
    // 
    //   <Table sx={{ minWidth: 650 }} aria-label="simple table">
    //     <TableHead>
    //       <TableRow>
    //         <TableCell>Product</TableCell>
    //         <TableCell align="right" className={classes.tableCell}>Price</TableCell>
    //         <TableCell align="right" className={classes.tableCell}>Quantity</TableCell>
    //         <TableCell align="right" className={classes.tableCell}>Subtotal</TableCell>
    //       </TableRow>
    //     </TableHead>
    //     <TableBody>
    //       {prodList.map((row, index) => {
    //         return <TableRow
    //           key={row.name}
    //           sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
    //         >
    //           <TableCell component="td" scope="row" className={`${classes.tableCell} product-field`}>
    //             <p className='prod-img'><img src={require(`../../static/images/${row.imageUrl}`).default} alt={row.name} /></p>
    //             <div>{row.name}<br />
    //             {row?.desc}</div>
    //             {/* </span> */}
    //           </TableCell>
    //           <TableCell align="left" className={classes.tableCell}>{row.price}</TableCell>
    //           <TableCell align="left" className={classes.tableCell}></TableCell>
    //           <TableCell align="left" className={classes.tableCell}>

    //             <span onClick={() => handleDelete(index)}></span>
    //           </TableCell>
    //         </TableRow>
    //       })}
    //     </TableBody>
    //   </Table>
    // </TableContainer>
  );
}
export default withStyles(styles)(ShoppingList);
